import * as d3 from "d3";
// import { curveCardinal, curveLinear, curveNatural } from "d3";
import React, { useRef, useEffect } from "react";

type propsStructure = {
  width: number;
  height: number;
  data: dataStructure;
};
type dataStructure = {
  date: number;
  temps: [number, number];
};

function Chart({ width, height, data }: any) {
  const ref = useRef(null);
  console.log(data);

  var margin = { top: 20, right: 30, bottom: 30, left: 40 };
  var dateDomain = d3.extent(data, (d: any) => parseInt(d.date));
  var tempRange = d3.extent(data, (d: any) => d.temps);
  console.log("tempRange:", tempRange);

  var newX = d3
    .scaleUtc()
    .domain(dateDomain as [number, number])
    .range([margin.left, width - margin.right]);

  var y = d3
    .scaleLinear()
    .domain([65, 90])
    .range([height - margin.bottom, margin.top]);

  useEffect(() => {
    const svg = d3.select(ref.current);

    svg
      .attr("width", width)
      .attr("height", height)
      .style("border", "1px solid black");

    svg.append("g").call(yAxis);
    svg.append("g").call(xAxis);
    svg
      .append("path")
      .datum(data)
      .attr("fill", "none")
      .attr("stroke", "steelblue")
      .attr("stroke-width", 1.5)
      .attr("stroke-linejoin", "round")
      .attr("stroke-linecap", "round")
      .attr("d", line);
  }, [data]);

  let line = d3
    .line()
    .x((d: any) => newX(d.date))
    .y((d: any) => y(parseFloat(d.temps[0])));

  let yAxis = (g: any) =>
    g
      .attr("transform", `translate(${margin.left}, 0)`)
      .call(d3.axisLeft(y))
      .call((g: any) => g.select(".domain").remove())
      .call(
        (g: any) =>
          g
            .select(".tick:last-of-type text")
            .clone()
            .attr("x", 3)
            .attr("text-anchor", "start")
            .attr("font-weight", "bold")
        // .text(data.y))
      );

  let xAxis = (g: any) =>
    g.attr("transform", `translate(0,${height - margin.bottom})`).call(
      d3
        .axisBottom(newX)
        .ticks(width / 80)
        .tickSizeOuter(0)
    );

  const draw = () => {
    const svg = d3.select(ref.current);
    var selection = svg.selectAll("rect").data(data);

    // selection
    //   .transition()
    //   .duration(300)
    //   .attr("height", (d: any) => y(d))
    //   .attr("y", (d: any) => height - y(d));

    selection
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 45 + 45)
      .attr("y", (d) => height)
      .attr("width", 40)
      .attr("height", 0)
      .attr("fill", "orange")
      .transition()
      .duration(1000)
      .attr("height", (d: any) => y(d))
      .attr("y", (d: any) => height - y(d));

    // selection
    //   .exit()
    //   .transition()
    //   .duration(300)
    //   .attr("y", (d) => height)
    //   .attr("height", 0)
    //   .remove();
  };

  return (
    <div className="chart">
      <svg ref={ref}></svg>
    </div>
  );
}

export default Chart;
