import * as d3 from "d3";
import React, { useRef, useEffect } from "react";

function BarChart({ width, height, data }: any) {
  const ref = useRef(null);

  var yScale = d3
    .scaleLinear()
    .domain([0, 60])
    .range([0, height - 100]);

  useEffect(() => {
    const svg = d3.select(ref.current);

    svg
      .attr("width", width)
      .attr("height", height)
      .style("border", "1px solid black");
    // .append("g")
    // .call(d3.axisRight(yScale));
  }, []);

  useEffect(() => {
    draw();
    d3.select(ref.current).append("g").call(d3.axisTop(yScale));
  }, [data]);

  const draw = () => {
    const svg = d3.select(ref.current);
    var selection = svg.selectAll("rect").data(data);

    // selection
    //   .transition()
    //   .duration(300)
    //   .attr("height", (d: any) => yScale(d))
    //   .attr("y", (d: any) => height - yScale(d));

    selection
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 45)
      .attr("y", (d) => height)
      .attr("width", 40)
      .attr("height", 0)
      .attr("fill", "orange")
      .transition()
      .duration(1000)
      .attr("height", (d: any) => yScale(d))
      .attr("y", (d: any) => height - yScale(d));

    // selection
    //   .exit()
    //   .transition()
    //   .duration(300)
    //   .attr("y", (d) => height)
    //   .attr("height", 0)
    //   .remove();
  };

  return (
    <div className="chart">
      <svg ref={ref}></svg>
    </div>
  );
}

export default BarChart;
