import React, { Component } from "react";
import { Link, useLocation, RouteComponentProps } from "react-router-dom";
import Images2016 from "./assets/images/feed/Images2016";

type ImageState = {
  index: number;
};

type TParams = { startID: string };

export default class FullView extends Component<
  { matches: RouteComponentProps<TParams> },
  ImageState
> {
  constructor(props: any) {
    super(props);
    this.state = { index: parseInt(props.match.params.startID) };

    // this.state = { index: 3 };
    console.log(props.match.params.startID);

    this.handleArrow = this.handleArrow.bind(this);
  }

  componentDidMount() {
    window.addEventListener("keydown", this.handleArrow);
  }
  componentWillUnmount() {
    window.removeEventListener("keydown", this.handleArrow);
  }

  handleArrow(event: any) {
    if (event.keyCode == 39) {
      //Right
      this.setState({ index: this.state.index + 1 });
    } else if (event.keyCode == 37) {
      //Left
      this.setState({ index: this.state.index - 1 });
    }
  }

  render() {
    return (
      <div>
        <div className="d-flex justify-content-end">
          <Link to={"/feed"} style={{ color: "#999999" }}>
            <i className="fas fa-times mr-5 mt-2"></i>
          </Link>
        </div>
        <div className="d-flex align-items-center justify-content-center">
          <i className="fas fa-chevron-left pr-4"></i>
          <div className="d-flex justify-content-center mt-0">
            <img
              className="img-fluid"
              src={Images2016[this.state.index].src}
              alt={Images2016[this.state.index].title}
              style={{
                maxHeight: "94vh",
                objectFit: "contain",
              }}
            ></img>
          </div>
          <i className="fas fa-chevron-right pl-4"></i>
        </div>
      </div>
    );
  }
}
