import * as d3 from "d3";
import { zoomTransform } from "d3";
import React, { useRef, useEffect, useState } from "react";

type propsStructure = {
  width: number;
  height: number;
  data: dataStructure;
};
type dataStructure = {
  date: number;
  temps: [string, string];
};

function Chart({ width, height, data }: any) {
  const ref = useRef(null);
  const [currentZoomState, setCurrentZoomState] = useState();

  useEffect(() => {
    const svg = d3.select(ref.current);
    var margin = { top: 20, right: 50, bottom: 30, left: 40 };
    var dateDomain = d3.extent(data, (d: any) => parseInt(d.date));
    var tempRange = d3.extent(data, (d: any) => parseFloat(d.temps[0]));

    var scaleX = d3
      .scaleUtc()
      .domain(dateDomain as [number, number])
      .range([margin.left, width - margin.right]);

    if (currentZoomState) {
      let newXScale = (currentZoomState as any).rescaleX(scaleX);
      scaleX.domain(newXScale.domain());
    }

    var scaleY = d3
      .scaleLinear()
      .domain(tempRange as [number, number])
      .nice()
      .range([height - margin.bottom, margin.top]);

    function yAxis(g: any) {
      g.attr("transform", `translate(${margin.left}, 0)`)
        .call(d3.axisLeft(scaleY))
        .call((g: any) => g.select(".domain").remove())
        .call((g: any) =>
          g
            .select(".tick:last-of-type text")
            .clone()
            .attr("x", 3)
            .attr("text-anchor", "start")
            .attr("font-weight", "bold")
            .text("Temperature")
        );
    }

    function xAxis(g: any) {
      g.attr("transform", `translate(0,${height - margin.bottom})`).call(
        d3
          .axisBottom(scaleX)
          .ticks(width / 80)
          .tickSizeOuter(0)
      );
    }

    let lineGenerator = d3
      .line()
      .x((d: any) => scaleX(d.date))
      .y((d: any) => scaleY(parseFloat(d.temps[0])));

    function linePath(g: any) {
      g.datum(data)
        .attr("fill", "none")
        .attr("stroke", "steelblue")
        .attr("stroke-width", 1.5)
        .attr("stroke-linejoin", "round")
        .attr("stroke-linecap", "round")
        .attr("d", lineGenerator);
    }

    var lastTime: number;
    var lastTemp: number;
    if (data) {
      try {
        let index = data.length - 1;
        lastTime = data[index].date;
        lastTemp = parseFloat(data[index].temps[0]);
      } catch (error) {}
    }
    function lineLabel(g: any) {
      g.attr(
        "transform",
        "translate(" + (width - margin.left) + "," + scaleY(lastTemp) + ")"
      )
        .attr("dy", ".35em")
        .attr("text-anchor", "start")
        .style("fill", "steelblue")
        .text(lastTemp);
    }
    function endCircle(g: any) {
      if (scaleY(lastTemp)) {
        g.attr("fill", "steelblue")
          .attr(
            "transform",
            "translate(" + scaleX(lastTime) + "," + scaleY(lastTemp) + ")"
          )
          .attr("r", 3);
      }
    }

    svg
      .attr("width", width)
      .attr("height", height)
      .style("border", "1px solid black");

    // svg.append("g").call(yAxis);
    // svg.append("g").call(xAxis);
    svg.select(".x-axis").call(xAxis);
    svg.select(".y-axis").call(yAxis);

    svg.append("text").call(lineLabel);
    svg.append("circle").call(endCircle);
    // svg.append("path").call(linePath);
    svg
      .select(".graphContent")
      .selectAll(".myLine")
      .data([data])
      .join("path")
      .attr("class", "myLine")
      .attr("stroke", "steelblue")
      .attr("fill", "none")
      .attr("stroke-width", 1.5)
      .attr("stroke-linejoin", "round")
      .attr("stroke-linecap", "round")
      .attr("d", lineGenerator);

    var zoom: any = d3
      .zoom()
      .scaleExtent([1, 10])
      // .extent([[margin.left, 0], [width - margin.right, height]])
      // .translateExtent([
      //   [margin.left, -Infinity],
      //   [width - margin.right, Infinity],
      // ])
      .translateExtent([
        [-margin.left, 0],
        [width - margin.right, height],
      ])
      .on("zoom", () => {
        const zoomState = zoomTransform(svg.node() as any);
        setCurrentZoomState(zoomState as any);
      });

    svg.call(zoom);
  }, [currentZoomState, data]);

  return (
    <div className="chart">
      <svg ref={ref}>
        <g className="graphContent"></g>
        <g className="x-axis"></g>
        <g className="y-axis"></g>
      </svg>
    </div>
  );
}

export default Chart;
