import React from "react";
import Images2016 from "./Images2016";
import { Route, Link } from "react-router-dom";

export default function routes2016() {
  React.useEffect(() => {
    window.addEventListener("keydown", handleRightArrow);

    return () => {
      window.removeEventListener("keydown", handleRightArrow);
    };
  }, []);
  function handleRightArrow(event: any) {
    //39 right, 37 left
    if (event.charCode == 39) {
      console.log("right");
    }
  }
  let routes = Images2016.map((image, index, Images2016) => {
    let leftButton;
    let rightButton;
    if (index === 0) {
      rightButton = (
        <Link to={"/feed" + Images2016[index + 1].src}>
          <i className="fas fa-chevron-right pl-4"></i>
        </Link>
      );
    } else if (index === Images2016.length - 1) {
      leftButton = (
        <Link to={"/feed" + Images2016[index - 1].src}>
          <i className="fas fa-chevron-left pr-4"></i>
        </Link>
      );
    } else {
      leftButton = (
        <Link to={"/feed" + Images2016[index - 1].src}>
          <i className="fas fa-chevron-left pr-4"></i>
        </Link>
      );
      rightButton = (
        <Link to={"/feed" + Images2016[index + 1].src}>
          <i className="fas fa-chevron-right pl-4"></i>
        </Link>
      );
    }

    return (
      <Route exact path={"/feed" + image.src}>
        <div className="d-flex align-items-center justify-content-center">
          {leftButton}
          <div className="d-flex justify-content-center mt-4">
            <img
              className="img-fluid"
              src={image.src}
              alt={image.title}
              style={{
                maxHeight: "92vh",
                objectFit: "contain",
              }}
            ></img>
          </div>
          {rightButton}
        </div>
      </Route>
    );
  });
  return routes;
}
