import * as d3 from "d3";
import { schemeGnBu } from "d3";
import React, { useRef, useEffect } from "react";

type propsStructure = {
  width: number;
  height: number;
  data: dataStructure;
};
type dataStructure = {
  date: number;
  temps: [string, string];
};

function Chart({ width, height, data }: any) {
  const ref = useRef(null);

  var margin = { top: 20, right: 50, bottom: 30, left: 40 };
  var dateDomain = d3.extent(data, (d: any) => parseInt(d.date));
  var tempRange = d3.extent(data, (d: any) => parseFloat(d.temps[0]));

  var scaleX = d3
    .scaleUtc()
    .domain(dateDomain as [number, number])
    .range([margin.left, width - margin.right]);

  var scaleY = d3
    .scaleLinear()
    .domain(tempRange as [number, number])
    .nice()
    .range([height - margin.bottom, margin.top]);

  useEffect(() => {
    const svg = d3.select(ref.current);

    svg.attr("width", width).attr("height", height);
    // .style("border", "1px solid black");

    svg.append("g").call(yAxis);
    svg.append("g").call(xAxis);
    svg.append("text").call(lineLabel);
    svg.append("circle").call(endCircle);
    svg
      .append("path")
      .datum(data)
      .attr("fill", "none")
      .attr("stroke", "steelblue")
      .attr("stroke-width", 1.5)
      .attr("stroke-linejoin", "round")
      .attr("stroke-linecap", "round")
      .attr("d", line);
  }, [data]);

  let line = d3
    .line()
    .x((d: any) => scaleX(d.date))
    .y((d: any) => scaleY(parseFloat(d.temps[0])));

  let yAxis = (g: any) =>
    g
      .attr("transform", `translate(${margin.left}, 0)`)
      .call(d3.axisLeft(scaleY))
      .call((g: any) => g.select(".domain").remove())
      .call((g: any) =>
        g
          .select(".tick:last-of-type text")
          .clone()
          .attr("x", 3)
          .attr("text-anchor", "start")
          .attr("font-weight", "bold")
          .text("Temperature")
      );

  function xAxis(g: any) {
    g.attr("transform", `translate(0,${height - margin.bottom})`).call(
      d3
        .axisBottom(scaleX)
        .ticks(width / 80)
        .tickSizeOuter(0)
    );
  }
  var lastTime: number;
  var lastTemp: number;
  if (data) {
    try {
      let index = data.length - 1;
      lastTime = data[index].date;
      lastTemp = parseFloat(data[index].temps[0]);
    } catch (error) {}
  }
  function lineLabel(g: any) {
    g.attr(
      "transform",
      "translate(" + (width - margin.left) + "," + scaleY(lastTemp) + ")"
    )
      .attr("dy", ".35em")
      .attr("text-anchor", "start")
      .style("fill", "steelblue")
      .text(lastTemp);
  }
  function endCircle(g: any) {
    g.attr("fill", "steelblue")
      .attr(
        "transform",
        "translate(" + scaleX(lastTime) + "," + scaleY(lastTemp) + ")"
      )
      .attr("r", 3);
  }

  const draw = () => {
    const svg = d3.select(ref.current);
    var selection = svg.selectAll("rect").data(data);

    // selection
    //   .transition()
    //   .duration(300)
    //   .attr("height", (d: any) => y(d))
    //   .attr("y", (d: any) => height - y(d));

    selection
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 45 + 45)
      .attr("y", (d) => height)
      .attr("width", 40)
      .attr("height", 0)
      .attr("fill", "orange")
      .transition()
      .duration(1000)
      .attr("height", (d: any) => scaleY(d))
      .attr("y", (d: any) => height - scaleY(d));

    // selection
    //   .exit()
    //   .transition()
    //   .duration(300)
    //   .attr("y", (d) => height)
    //   .attr("height", 0)
    //   .remove();
  };

  return (
    <div className="chart">
      <svg ref={ref}></svg>
    </div>
  );
}

export default Chart;
