import React from "react";
import { Image } from "./createImageList";

export function threeCols(images: Image[], startID: number, endID: number) {
  let col1: any = [];
  let col2: any = [];
  let col3: any = [];

  images.forEach((image) => {
    if (image.id < startID || image.id > endID) {
      return;
    } else {
      switch (image.col) {
        case 1:
          col1.push(
            <img
              src={image.src}
              className="img-fluid pb-3"
              alt={image.title}
            ></img>
          );
          break;
        case 2:
          col2.push(
            <img
              src={image.src}
              className="img-fluid pb-3"
              alt={image.title}
            ></img>
          );
          break;
        case 3:
          col3.push(
            <img
              src={image.src}
              className="img-fluid pb-3"
              alt={image.title}
            ></img>
          );
          break;
      }
    }
  });

  return (
    <div className="row row-cols-1 row-cols-sm-1 row-cols-md-2 row-cols-lg-3 ">
      <div className="col px-md-1 text-center">{col1}</div>
      <div className="col px-md-1 text-center">{col2}</div>
      <div className="col px-md-1 text-center">{col3}</div>
    </div>
  );
}

// pano: 2/3 left, 1/3 right
export function twoColsPanoLeft(imagePano: Image, imageRight: Image) {
  return (
    <div className="row row-sm-1 row-md-2 row-lg-3">
      <div className="col-md-8 px-md-2 text-center">
        <img
          src={imagePano.src}
          className="img-fluid pb-3"
          alt={imagePano.title}
        ></img>
      </div>
      <div className="col-md-4 px-md-2 text-center">
        <img
          src={imageRight.src}
          className="img-fluid pb-3"
          alt={imageRight.title}
        ></img>
      </div>
    </div>
  );
}

// pano: 1/3 left, 2/3 right
export function twoColsPanoRight(imagePano: Image, imageLeft: Image) {
  return (
    <div className="row row-sm-1 row-md-2 row-lg-3">
      <div className="col-md-4 px-md-2 text-center">
        <img
          src={imageLeft.src}
          className="img-fluid pb-3"
          alt={imageLeft.title}
        ></img>
      </div>
      <div className="col-md-8 px-md-2 text-center">
        <img
          src={imagePano.src}
          className="img-fluid pb-3"
          alt={imagePano.title}
        ></img>
      </div>
    </div>
  );
}

export function oneColPano(imagePano: Image) {}

