import React from "react";
import { JsxFlags } from "typescript";
import firebase from "../initFirebase";
import Chart from "./Chart";

type weatherState = {
  rawData: any;
};

type rawDataStructure = {
  date: number;
  temps: any;
};

export default class WeatherPage extends React.Component<{}, weatherState> {
  constructor(props: any) {
    super(props);
    this.state = { rawData: [] };

    this.getRawData = this.getRawData.bind(this);
  }
  componentDidMount() {
    this.getRawData();
  }
  // data = [10, 40, 30, 20, 50, 10, 60, 0];

  getRawData() {
    var tempsRef = firebase.database().ref("/temps/");
    tempsRef.on("value", (snapshot) => {
      let list = Object.entries(snapshot.val());
      let newList: rawDataStructure[] = list.map(([key, value]) => {
        return {
          // date: parseInt(entry[0] + "000"),
          // temps: [parseFloat(entry[1][0]), parseFloat(entry[1][1])],
          date: parseInt(key + "000"),
          temps: value,
        };
      });
      this.setState({ rawData: newList });
    });
    console.log("got data");
  }

  makeSVG() {
    return (
      <div>
        <svg style={{ border: "2px solid gold" }}></svg>
        <svg>
          <circle cx="150" cy="77" r="40" />
        </svg>
      </div>
    );
  }

  render() {
    // console.log(this.state.rawData);

    return (
      <div className="container">
        weather page
        <div>
          {/* {this.makeSVG()} */}
          {/* {this.state.rawData.map((entry: any) => (
            <div>{entry[0] + " " + entry[1][0]} </div>
          ))} */}
        </div>
        {this.state.rawData ? (
          <Chart width={1000} height={600} data={this.state.rawData} />
        ) : (
          <div></div>
        )}
      </div>
    );
  }
}
